var payload = msg.payload;
switch(msg.topic){
    case "dr/lat_lon":
        v.basic_state.lat = payload.latitude;
        v.basic_state.lon = payload.longitude;
        break;

    case "dr/yaw":
        v.basic_state.heading = to_northing(payload.data);
        break;

    case "dr/roll":
        v.basic_state.roll = to_deg(payload.data);
        break;

    case "dr/pitch":
        v.basic_state.pitch = to_deg(payload.data);
        break;

    case "dr/depth":
        v.basic_state.depth = payload.data;
        break;

    case "dr/altitude":
        v.basic_state.altitude = payload.data;
        break;        

    case "core/vbs_fb":
        v.basic_state.vbs = payload.value;
        break;

    case "core/lcg_fb":
        v.basic_state.lcg = payload.value;
        break;

    case "core/thruster1_fb":
        v.basic_state.t1 = payload.rpm.rpm;
        break;

    case "core/thruster2_fb":
        v.basic_state.t2 = payload.rpm.rpm;
        break;

    case "core/battery":
        v.basic_state.batt_v = payload.voltage;
        break;

    case "core/gps":
        v.gps_fix.lat = payload.latitude;
        v.gps_fix.lon = payload.longitude;
        v.gps_fix.name = v.basic_state.name + "_gps_fix"
        break;

    case "bt/last_wp":
        if(payload.pose.header.frame_id == "utm"){
            v.bt_state.current_wp = payload;
        }
        break;

    case "bt/tip":
        v.bt_state.tip.name = payload.name;
        v.bt_state.tip.message = payload.message;
        switch(payload.status){
            case 0:
                v.bt_state.tip.status = "unknown"
                break
            case 1:
                v.bt_state.tip.status = "invalid"
                break
            case 2:
                v.bt_state.tip.status = "running"
                break
            case 3:
                v.bt_state.tip.status = "success"
                break
            case 4:
                v.bt_state.tip.status = "failure"
                break
        }
        break;

    case "bt/live_wp/enable":
        v.bt_state.live_wp_enable = payload.data;
        break;

    case "bt/algae_farm/enable":
        v.bt_state.algae_farm_enable = payload.data;
        break;

    case "bt/gui_wp/enable":
        v.bt_state.gui_wp_enable = payload.data;
        break;

    case "bt/heartbeat":
        v.bt_state.last_heartbeat = Date.now()
        break;

    case "bt/mission_control":
        if(payload.command == 5){ // is_feedback
            v.bt_state.current_plan = payload
        }
}

