function make_tip_str(tip){
    c = "black"
    icon = ""
    switch(tip.status){
        case "failure":
            c = "red"
            icon = "❌"
            break
        case "success":
            c = "green"
            icon = "✅"
            break
        case "running":
            c = "blue"
            icon = "⚙️"
            break
    }

    tip_type = tip.name.slice(0,1)
    tip_name = tip.name.slice(2)

    t = ""
    if(tip.time > 10){
        t = new Date(tip.time).toTimeString().slice(0,8)
    }

    title = t + " " + icon + " <font color=" + c + ">" + tip_name
    msg = "<font color=" + c +">" + tip.message

    return [title, msg];
}

