// each mqtt_in node also emits the entire topic
// so we can switch robot name and which topic it is coming from
const [smarc, robot_name, ...topic_parts] = msg.topic.split('/');
mqtt_topic = topic_parts.join("/")

// then try to get this robot from the list of vehicles
// if its not there, construct an empty state object
robot_states = global.get("robot_states")

if(!(robot_states)){
    global.set("robot_states", {})
}

if(!(robot_name in robot_states)){
    robot_states[robot_name] = new_robot_state(robot_name)
}

robot_state = robot_states[robot_name]


robot_state.last_update = Date.now()

payload = msg.payload
switch(mqtt_topic){
    case "dr/lat_lon":
        robot_state.base.lat = payload.latitude;
        robot_state.base.lon = payload.longitude;
        robot_state.gui.dr.lat = payload.latitude;
        robot_state.gui.dr.lon = payload.longitude;
        break;

    case "dr/yaw":
        hdg = to_northing(payload.data)
        robot_state.base.heading = hdg;
        robot_state.gui.dr.heading = hdg;
        break;

    case "dr/roll":
        robot_state.base.roll = to_deg(payload.data);
        break;

    case "dr/pitch":
        robot_state.base.pitch = to_deg(payload.data);
        break;

    case "dr/depth":
        robot_state.base.depth = payload.data;
        break;

    case "dr/altitude":
        robot_state.base.altitude = payload.data;
        break;        

    case "core/vbs_fb":
        robot_state.base.vbs = payload.value;
        break;

    case "core/lcg_fb":
        robot_state.base.lcg = payload.value;
        break;

    case "core/thruster1_fb":
        robot_state.base.t1 = payload.rpm.rpm;
        break;

    case "core/thruster2_fb":
        robot_state.base.t2 = payload.rpm.rpm;
        break;

    case "core/battery":
        robot_state.base.batt_v = payload.voltage;
        break;

    case "core/gps":
        robot_state.base.gps_lat = payload.latitude;
        robot_state.base.gps_lon = payload.longitude;
        robot_state.gui.gps.lat = payload.latitude;
        robot_state.gui.gps.lon = payload.longitude;
        break;

    case "bt/last_wp":
        robot_state.bt.current_wp = payload;
        break;

    case "bt/tip":
        robot_state.bt.tip.name = payload.name;
        robot_state.bt.tip.message = payload.message;
        status_str = {
            0:"unknown",
            1:"invalid",
            2:"running",
            3:"success",
            4:"failure"
        }
        robot_state.bt.tip.status = status_str[payload.status]
        break;

    case "bt/live_wp/enable":
        robot_state.bt.live_wp_enable = payload.data;
        break;

    case "bt/algae_farm/enable":
        robot_state.bt.algae_farm_enable = payload.data;
        break;

    case "bt/gui_wp/enable":
        robot_state.bt.gui_wp_enable = payload.data;
        break;

    case "bt/heartbeat":
        robot_state.bt.last_heartbeat = Date.now()
        break;

    case "bt/mission_control":
        if(payload.command == 5){ // is_feedback
            robot_state.bt.current_plan = payload
        }
}

